mapper.select:
select m.menu_id,m.parent_id,m.menu_name,m.sort,m.menu_type,m.path,m.component_path,m.perms,m.icon from menu m left join role_to_menu rtm on m.menu_id = rtm.menu_id where ( m.menu_type = 'DIRECTORY'
OR ( rtm.role_id IN
#{roleId}
AND m.status = 0 )
)

SELECT * FROM menu
WHERE parent_id IN

    #{parentId}

WITH RECURSIVE MenuTree AS (
SELECT * FROM menu WHERE parent_id IN

    #{parentId}

UNION ALL
SELECT m.* FROM menu m INNER JOIN MenuTree mt ON m.parent_id = mt.menu_id
)
SELECT * FROM MenuTree ORDER BY sort;

SELECT COUNT(*)
FROM menu



            parent_id = #{parentId}

                AND menu_name = #{menuName}


                AND path = #{path}


                AND menu_id != #{menuId}




        WHERE 1 = 0

SELECT menu_id
FROM menu
WHERE parent_id = #{menuId}

WITH RECURSIVE MenuTree AS (
SELECT menu_id FROM menu WHERE parent_id IN

    #{parentId}

UNION ALL
SELECT m.menu_id FROM menu m INNER JOIN MenuTree mt ON m.parent_id = mt.menu_id
)
SELECT menu_id FROM MenuTree;

mapper.delete:
DELETE FROM menu
WHERE menu_id IN

    #{menuId}

DELETE FROM role_to_menu
WHERE menu_id IN

    #{menuId}
